import { Component, OnInit } from '@angular/core';
import { ActifService } from '../../services/actif.service';
import { Actif } from '../../models/actif.model';

@Component({
  selector: 'app-list-actifs',
  templateUrl: './list-actifs.component.html',
  styleUrls: ['./list-actifs.component.css']
})
export class ListActifsComponent implements OnInit {

  actifs: Actif[] = [];

  constructor(private actifService: ActifService) {}

  ngOnInit(): void {
    this.actifService.getActifs().subscribe(data => {
      this.actifs = data;
    });
  }
}
<!-- Main Content Wrapper -->
<div class="content-wrapper">
  <div class="content pt-4"> <!-- Added padding-top -->
    <div class="container-fluid">
      <div class="row">
        <!-- Sidebar (HomeComponent) occupying 4/12 -->
        <div class="col-md-4">
          <app-home></app-home>
        </div>

        <!-- Main content occupying 8/12 -->
        <div class="col-md-8">
          <div class="card"> <!-- Added card to wrap the content -->
            <div class="card-header">
              <h5 class="card-title">List of Actifs</h5>
            </div>
            <div class="card-body">
              <div *ngIf="actifs.length === 0">No actifs found.</div>
              <ul *ngIf="actifs.length > 0">
                <li *ngFor="let actif of actifs">
                  {{ actif.nom }} - {{ actif.description }}
                </li>
              </ul>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
</div>
