import { Injectable } from '@angular/core';
import { HttpClient, Htt } from '@angular/common/http';
import { Observable } from 'rxjs';
import { Actif } from '../models/actif.model'; // Adjust the path as needed

@Injectable({
  providedIn: 'root'
})
export class ActifService {
  private apiUrl = 'http://localhost:9000/actif/getAll'; // Adjust the URL if needed


  httpOptions = {
    headers: new HttpHeaders({
      'Content-Type': 'application/json'
    })
  };

  constructor(private http: HttpClient) { }

  getAllActifs(): Observable<Actif[]> {
    return this.http.get<Actif[]>(this.apiUrl);
  }
}
