import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { Actif } from '../models/actif.model'; // Adjust the path as needed

@Injectable({
  providedIn: 'root'
})
export class ActifService {
  private apiUrl = 'http://localhost:9000/actifs'; // Adjust the URL if needed

  constructor(private http: HttpClient) { }

  getAllActifs(): Observable<Actif[]> {
    return this.http.get<Actif[]>(this.apiUrl);
  }

  getActifById(id: number): Observable<Actif> {
    return this.http.get<Actif>(`${this.apiUrl}/${id}`);
  }

  createActif(actif: Actif): Observable<Actif> {
    return this.http.post<Actif>(this.apiUrl, actif);
  }

  updateActif(id: number, actif: Actif): Observable<Actif> {
    return this.http.put<Actif>(`${this.apiUrl}/${id}`, actif);
  }

  deleteActif(id: number): Observable<void> {
    return this.http.delete<void>(`${this.apiUrl}/${id}`);
  }
}
